package com.syz.springangular.demo;

import java.text.SimpleDateFormat;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Optional;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.CommandLineRunner;
import org.springframework.stereotype.Component;

import com.syz.springangular.demo.models.Book;
import com.syz.springangular.demo.models.BookTag;
import com.syz.springangular.demo.models.Review;
import com.syz.springangular.demo.repository.BookRepository;
import com.syz.springangular.demo.repository.BookTagRepository;
import com.syz.springangular.demo.utils.SharedParameters;

//interface CommandLineRunner class means function in run(String... args)
//will run when spring boot application starts
@Component
public class DbSeeder implements CommandLineRunner {
	
	
	private BookRepository bookRepository;
	private BookTagRepository bookTagRepository;
	
	private Map<String, Integer> initTags;
	
	
	@Autowired
	public DbSeeder(BookRepository bookRepository, BookTagRepository bookTagRepository) {
		this.bookRepository = bookRepository;
		this.bookTagRepository = bookTagRepository;
		
		initTags = new HashMap<String, Integer>();
		
	}
	
	@Value("${mode.debug}")
	private boolean debugMode;

	@Override
	public void run(String... args) throws Exception {

		System.out.println("\n\n\n");
		//clean up the database
		if(debugMode) {
		    System.out.println("Delete All Data and starting anew");
		    this.bookRepository.deleteAll();
			System.out.println("Initialize Tags");
					
	//		SharedParameters.booktags = this.bookTagRepository.findAll();
			
			
			System.out.println("Prepare Mongo DB");
			String reviewMsg;
			
			SimpleDateFormat fmt = new SimpleDateFormat("yyyy-MM-dd");
		
			Book sample1 = new Book("The Golden Thread: How Fabric Changed History", 
			        Arrays.asList("Kassia St Clair"),
			        fmt.parse("2019-12-02"),
			        Arrays.asList("history", "culture"));
			sample1.setImageUrl("data:image/jpeg;base64,");
			updateHashMap("history");
			updateHashMap("culture");
			
			Book sample2 = new Book("The Altruists", 
			        Arrays.asList("Andrew Ridker"),
			        fmt.parse("2019-03-02"),
			        Arrays.asList("fiction", "comic"));
			sample2.setImageUrl("data:image/jpeg;base64,");
			updateHashMap("fiction");
			updateHashMap("comic");
			
			Book sample3 = new Book("Then the Fish Swallowed Him", 
			        Arrays.asList("Amir Ahmadi Arian"),
			        fmt.parse("2020-03-02"),
			        Arrays.asList("fiction", "foreign"));
			sample3.setImageUrl("data:image/jpeg;base64,");
			updateHashMap("fiction");
			updateHashMap("foreign");
			
			Book sample4 = new Book("Permanent Record", 
	                Arrays.asList("Edward Snowden"),
	                fmt.parse("2019-09-02"),
	                Arrays.asList("memoir", "snowden", "current affair"));
			sample4.setImageUrl("data:image/jpeg;base64,");
			updateHashMap("memoir");
			updateHashMap("snowden");
			updateHashMap("current affair");
			
			Book sample5 = new Book("I am Dynamite! A Life of Friedrich Nietzsche", 
	                Arrays.asList("Sue Prideaux"),
	                fmt.parse("2018-10-02"),
	                Arrays.asList("biography", "nietzsche"));
			sample5.setImageUrl("data:image/jpeg;base64,");
			updateHashMap("biography");
			updateHashMap("nietzsche");
			
			Book sample6 = new Book("Capital and Ideology", 
	                Arrays.asList("Thomas Piketty"),
	                fmt.parse("2020-03-02"),
	                Arrays.asList("economics", "politics"));
			sample6.setImageUrl("data:image/jpeg;base64,");
			updateHashMap("economics");
			updateHashMap("politics");
			
			Book sample7 = new Book("Deaths of Despair and the Future of Capitalism ", 
	                Arrays.asList("Angus Deaton", "Anne Case"),
	                fmt.parse("2020-03-02"),
	                Arrays.asList("economics", "politics"));
	        sample7.setImageUrl("data:image/jpeg;base64,");
	        updateHashMap("economics");
	        updateHashMap("politics");
	        
	        Book sample8 = new Book("Submission", 
	                Arrays.asList("Michel Houellebecq"),
	                fmt.parse("2016-10-02"),
	                Arrays.asList("fiction", "french"));
	        sample8.setImageUrl("data:image/png;base64,");
	        updateHashMap("fiction");
	        updateHashMap("french");
	        
	        Book sample9 = new Book("Serotonin", 
	                Arrays.asList("Michel Houellebecq"),
	                fmt.parse("2019-11-02"),
	                Arrays.asList("fiction", "french"));
	        sample9.setImageUrl("data:image/jpeg;base64,");
	        updateHashMap("fiction");
	        updateHashMap("french");
	        
	        Book sample10 = new Book("Golden Child", 
	                Arrays.asList("Claire Adam"),
	                fmt.parse("2019-01-02"),
	                Arrays.asList("fiction", "foreign"));
	        sample10.setImageUrl("data:image/png;base64,");
	        updateHashMap("fiction");
	        updateHashMap("foreign");
	        
	        reviewMsg = "In an enlivening history of fabric, St. Clair takes aim at those who trivialize the study of clothing. “We live surrounded by cloth,” she writes. “We are swaddled in it at birth and shrouds are drawn over our faces in death.” Rather than a broad overview, St. Clair provides a cleverly structured patchwork of vignettes spanning the eras and the continents: Paleolithic bast threads excavated in a cave in Georgia; the patterns of ancient China’s silks (“mirrored flower,” “nimble waves”); the inflatable rubber-and-nylon suits that weighed down Mercury astronauts as they journeyed into space. Her descriptions of textiles and the people who created and wore them are sensual and even moving.";
	        Review review01_1 = new Review(SharedParameters.NEW_YORKER);
	        review01_1.setMessage(reviewMsg);
	        sample1.addReview(review01_1);
	        Review review01_2 = new Review(SharedParameters.ECONOMIST);
	        review01_2.setReviewUrl("https://www.economist.com/books-and-arts/2018/12/01/books-by-the-economists-writers");
	        sample1.addReview(review01_2);
	        
	        
			List<Book> initBooks = Arrays.asList(sample1, sample2, sample3, sample4, 
			        sample5, sample6, sample7, sample8, sample9, sample10);
			
			bookRepository.saveAll(initBooks);
			initTags.forEach((k,v) ->{
				bookTagRepository.save(new BookTag(k, v));
			});
		}
		
	}
	
	private void updateHashMap(String tagName) {
		Integer count = Optional.ofNullable(initTags.get(tagName)).orElse(0);
		count++;
		initTags.put(tagName, count);
	}

}
